import { useState } from 'react';
import ItemCart from './ItemCart'

//cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc);

const Cart = ({cart,changeUnit}) => {
    const refreshPriceCart = cart => cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc);
    /* const [price,setPrice] = useState( cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc) );

    setPrice( cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc) ); */
    /* const [price,setPrice] = useState(0);

    setPrice( cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc) ); */

    return (
        <div id="shopping-cart">
            <header className="name_cart">Mi carrito<hr/></header>
            <div className="item-list scroll">
                {
                    cart.length === 0 ? <div className="text-center text-grey">El carrito está vacío</div> : cart.map( (e,i) => <ItemCart item={e} key={i} cart={cart} changeUnit={changeUnit} onAddUnitItem={refreshPriceCart}/> )                
                }
            </div>
            <footer>
                <hr/>
                {/* <div className="text-right">Total a pagar: { price }€</div> */}
                <div className="text-right">Total a pagar: { refreshPriceCart(cart) }€</div>
                {/* <div className="text-right">Total a pagar: { cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc) }€</div> */}
            </footer>
        </div>
    )
}

export default Cart;
