import { useState } from "react";

const ItemList = ({db,item,id,addItem}) => {
    const [intoCart,setIntoCart] = useState(0);
    return (
        <div key={id}>
            <div>{item.name}</div>
            <button type="button" 
            onClick={ () => addItem( db.filter( e => e.id == item.id )[0] ) } >
                Agregar al carrito
            </button>
        </div>
    )
}

export default ItemList;
