import { useEffect } from "react";

const borderInput = () => {
    document.getElementById("buscador").style.border = "3px solid orange";
}

const Nav = () => {
    return (
        <nav>
            <div className="menu_logo">
                <div id="hamburger_menu" className="nav_hover">
                    {/* <img src={"/icon/bars-solid.svg"} /> */}<img src={"/icon/menu-24px.svg"} />
                </div>
                <div className="logo nav_hover">
                    {/* <img src={"/logo/amazon_logo.png"} /> */}
                    {/* <div className="extension">.es</div> */}
                </div>
            </div>
            <div id="buscador">
                    <select name="" id="">
                        <option value="">Todos los departamentos &#x25BC;</option>
                    </select>
                    <input type="text" name="" id="" onclick={
                        () => {
                            useEffect( () => {
                                return () => { 
                                    document.getElementById("buscador").style.border = "3px solid orange";
                                }
                            } );
                        }
                    }/>
                    <button className="search_button"><img src={"/icon/search-solid.svg"} /></button>
            </div>
            <div>
                {/* <div className="nav_hover"></div>
                <div className="nav_hover"></div>
                <div className="nav_hover"></div>
                <div className="nav_hover"></div> */}
            </div>
            
        </nav>
    )
}

export default Nav;
