const ItemList = ({db,item,id,addItem}) => {

    return (
        <div key={id}>
            <div className="image-list"></div>
            <div>
                <div className="text-center bold">{item.name}</div>
                <hr/>
                { item.intoCart ? <div>Ya en la cesta</div> : 
                <button type="button" 
                onClick={ () => addItem( db.filter( e => e.id == item.id )[0] ) } >
                    Agregar al carrito
                </button>}
            </div>
        </div>
    )
}

export default ItemList;
