import ItemCart from './ItemCart'

const CART = [
    {
        id: 1,
        name: "Paleta Ibérica de Bellota de 4,5 kg Covap", //Paleta 100 % Ibérica de Bellota Alta Expresion Plata de Covap de 4,5 kg
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "jamon.png",
        price: 300,
        ud: 1
    },
    {
        id: 2,
        name: "Teclado Logitech",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 19.95,
        ud: 3
    },
];

const Cart = () => {
    return (
        <div id="shopping-cart">
            <header className="name_cart">Mi carrito<hr/></header>
            <div className="item-list scroll">
                {
                    CART.length === 0 ? <div className="text-center text-grey">El carrito está vacío</div> : CART.map( e => <ItemCart item={e} /> )                
                }
            </div>
            <footer>
                <hr/>
                <div className="text-right">Total a pagar: { CART.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc) }€</div>
            </footer>
        </div>
    )
}

export default Cart;
