import React, { useEffect, useState } from 'react';
import Nav from '../components/Nav';
import Cart from '../components/Cart';
import ItemList from '../components/ItemList';

const ITEMS_DB = [
    {
        id: 1,
        name: "Paleta Ibérica de Bellota de 4,5 kg Covap", //Paleta 100 % Ibérica de Bellota Alta Expresion Plata de Covap de 4,5 kg
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "jamon.png",
        price: 300,
        ud: 1
    },
    {
        id: 2,
        name: "Teclado Logitech",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 19.95,
        ud: 1
    },
    {
        id: 3,
        name: "Piano de cola",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 1800,
        ud: 1
    },
];

let CART = [
    {
        id: 1,
        name: "Paleta Ibérica de Bellota de 4,5 kg Covap", //Paleta 100 % Ibérica de Bellota Alta Expresion Plata de Covap de 4,5 kg
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "jamon.png",
        price: 300,
        ud: 1
    },
    {
        id: 2,
        name: "Teclado Logitech",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 19.95,
        ud: 1
    },
];

const Index = () => {
    const [shoppingCart,setShoppingCart] = useState(CART);
    const [totalPrice,setTotalPrice] = useState( shoppingCart.length == 0 ? 0 : shoppingCart.map( e => e.price*e.ud ).reduce( (e,acc) => e+acc ) );

    useEffect( () => {
        const totalItems = document.getElementById("cart-count");
        const priceCart  = document.getElementById("totalPrice");

        totalItems.innerHTML = shoppingCart.length;
        totalItems.style.left = (shoppingCart.length > 9 ? `21px` : `25px`);

        priceCart.innerHTML = `Total a pagar: ${totalPrice}€`;
        console.log("effect");
    });

    const setUnitItem = (id,ud) => {
        shoppingCart.filter( e => e.id == id )[0].ud = ud;
        setShoppingCart( shoppingCart );
        setTotalPrice( shoppingCart.length == 0 ? 0 : shoppingCart.map( e => e.price*e.ud ).reduce( (e,acc) => e+acc ) );
    }

    const deleteItem = id => { 
        setTotalPrice( (totalPrice - shoppingCart.filter( e => e.id == id ).map( e => e.price*e.ud )).toFixed(2) );
        setShoppingCart( shoppingCart.filter( e => e.id !== id ) );
    }

    const addItem = item => {
        //const priceCart  = document.getElementById("totalPrice");

        console.log(shoppingCart);
        console.log(item);
        setShoppingCart( [...shoppingCart, item]);
        console.log(totalPrice);
        setTotalPrice( shoppingCart.map( e => e.price*e.ud ).reduce( (e,acc) => e+acc ).toFixed(2) );
        console.log(totalPrice);
        console.log(shoppingCart);
        //priceCart.innerHTML = `Total a pagar: ${totalPrice}€`;

    }

    return (
        <div>
            <div>
                <Nav />
                <Cart /* onNewItem={addItemToCart} */ cart={shoppingCart} /* totalPrice={totalPrice} */ deleteItem={deleteItem} setUnitItem={setUnitItem} /* changeUnit={setUnitItem} */ />


                {
                    //console.log(ITEMS_DB.map( e => e ))
                    ITEMS_DB.map( e => <div>
                        <div>{e.name}</div>
                        <button type="button" onClick={ () => {
                            //onNewItem(e.id);
                            addItem( ITEMS_DB.filter( el => el.id == e.id )[0] );
                        }}>Agregar al carrito</button>
                    </div> )
                }
            </div>
            <div>
                
            </div>
        </div>
        
    )
}

export default Index;
