import ItemCart from './ItemCart'

const Cart = ({cart}) => {
    return (
        <div id="shopping-cart">
            <header className="name_cart">Mi carrito<hr/></header>
            <div className="scroll">
                {
                    cart.length === 0 ? <div className="text-center text-grey">El carrito está vacío</div> : cart.map( e => <ItemCart item={e} /> )                
                }
            </div>
            <footer>

            </footer>
        </div>
    )
}

export default Cart;
