const ItemList = ({db,item,id,addItem}) => {

    return (
        <div key={id}>
            <div>{item.name}</div>
            { item.intoCart ? <div>Ya en la cesta</div> : 
            <button type="button" 
            onClick={ () => addItem( db.filter( e => e.id == item.id )[0] ) } >
                Agregar al carrito
            </button>}
        </div>
    )
}

export default ItemList;
