import { useState } from 'react';
import ItemCart from './ItemCart'

//cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc);

const Cart = ({cart,changeUnit}) => {
    const [price,setPrice] = useState(cart.map( e => e.price*e.ud ).reduce( (e,acc) => e + acc));
    return (
        <div id="shopping-cart">
            <header className="name_cart">Mi carrito<hr/></header>
            <div className="item-list scroll">
                {
                    cart.length === 0 ? <div className="text-center text-grey">El carrito está vacío</div> : cart.map( (e,i) => <ItemCart item={e} key={i} changeUnit={changeUnit} refreshCart={refreshPrice}/> )                
                }
            </div>
            <footer>
                <hr/>
                <div className="text-right">Total a pagar: { refreshPrice() }€</div>
            </footer>
        </div>
    )
}

export default Cart;
