const ItemList = ({db,item,id,addItem}) => {
    return (
        <div key={i}>
            <div>{item.name}</div>
            <button type="button" onClick={ () => addItem( db.filter( e => e.id == item.id )[0] ) }>Agregar al carrito</button>
        </div>
    )
}

export default ItemList;
