import { useEffect } from "react";

const borderInput = () => {
    document.getElementById("buscador").style.border = "3px solid orange";
}

const Nav = () => {
    return (
        <nav>
            <div className="menu_logo">
                <div id="hamburger_menu" className="nav_hover">
                    {/* <img src={"/icon/bars-solid.svg"} /> */}<svg xmlns="http://www.w3.org/2000/svg" height="24" viewBox="0 0 24 24" width="24" className="svg_menu"><path d="M0 0h24v24H0z" fill="none"/><path d="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z"/></svg>
                </div>
                <div className="logo nav_hover">
                    {/* <img src={"/logo/amazon_logo.png"} /> */}
                    {/* <div className="extension">.es</div> */}
                </div>
            </div>
            <div id="buscador">
                    <select name="" id="">
                        <option value="">Todos los departamentos &#x25BC;</option>
                    </select>
                    <input type="text" name="" id="" onclick={
                        () => {
                            useEffect( () => {
                                return () => { 
                                    document.getElementById("buscador").style.border = "3px solid orange";
                                }
                            } );
                        }
                    }/>
                    <button className="search_button"><img src={"/icon/search-solid.svg"} /></button>
            </div>
            <div className="nav-tools">
                <div className="nav_hover">
                    <span className="span_line1">Hola, identifícate</span><br/><span className="span_line2">Cuenta y listas</span>
                </div>
                <div className="nav_hover">
                    <span className="span_line1">Devoluciones </span><br/><span className="span_line2">y Pedidos</span>
                </div>
                <div className="nav_hover">
                    <span className="span_line1">Suscríbete a </span><br/><span className="span_line2">Prime</span>
                </div>
                <div className="nav_hover"></div>
            </div>
            
        </nav>
    )
}

export default Nav;
