import { useEffect, useState } from 'react';
import ItemCart from './ItemCart';

const Cart = ({cart,totalPrice,setUnitItem,deleteItem}) => {

    return (
        <div id="shopping-cart">
            <header className="name_cart">Mi carrito<hr/></header>
            <div className="item-list scroll">
                {
                    cart.length === 0 ? 
                    <div className="text-center text-grey">El carrito está vacío</div> : 
                    cart.map( (e,i) => <ItemCart item={e} key={i} setUnitItem={setUnitItem} deleteItem={deleteItem}/> )
                }
            </div>
            <footer>
                { cart.length > 1 ? <div className="text-right error"><a>Vaciar cesta</a></div> : <div></div>}
                <hr/>
                <div className="text-right" id="totalPrice"></div>
            </footer>
        </div>
    )
}

export default Cart;
