import React, { useEffect, useState } from 'react';
import Nav from '../components/Nav';
import Cart from '../components/Cart';
import ItemList from '../components/ItemList';

const ITEMS_DB = [
    {
        id: 1,
        name: "Paleta Ibérica de Bellota de 4,5 kg Covap", //Paleta 100 % Ibérica de Bellota Alta Expresion Plata de Covap de 4,5 kg
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "jamon.png",
        price: 300,
        ud: 1,
        intoCart: false
    },
    {
        id: 2,
        name: "Teclado Logitech",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 19.95,
        ud: 1,
        intoCart: false
    },
    {
        id: 3,
        name: "Piano de cola",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 1800,
        ud: 1,
        intoCart: false
    },
    {
        id: 4,
        name: "Telescopio Meade MX8",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 834.95,
        ud: 1,
        intoCart: false
    },
];

let CART = [
    /* {
        id: 1,
        name: "Paleta Ibérica de Bellota de 4,5 kg Covap", //Paleta 100 % Ibérica de Bellota Alta Expresion Plata de Covap de 4,5 kg
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "jamon.png",
        price: 300,
        ud: 1
    },
    {
        id: 2,
        name: "Teclado Logitech",
        description: "ñdjabvndmnbñnbéñkah jerodbklja´ñ´ldkfbmnñdklfhjj n",
        img: "nd.png",
        price: 19.95,
        ud: 1
    }, */
];

const Index = () => {
    const [shoppingCart,setShoppingCart] = useState(CART);
    const [totalPrice,setTotalPrice] = useState( shoppingCart.length == 0 ? "0.00" : shoppingCart.map( e => e.price*e.ud ).reduce( (e,acc) => e+acc ).toFixed(2) );

    useEffect( () => {
        const totalItems = document.getElementById("cart-count");
        const priceCart  = document.getElementById("totalPrice");

        totalItems.innerHTML = shoppingCart.length;
        totalItems.style.left = (shoppingCart.length > 9 ? `21px` : `25px`);
        
        priceCart.innerHTML = `Total a pagar: ${totalPrice}€`;
    });

    const setUnitItem = (id,ud) => {
        shoppingCart.filter( e => e.id == id )[0].ud = ud;
        setShoppingCart( shoppingCart );
        setTotalPrice( shoppingCart.length == 0 ? 0 : shoppingCart.map( e => e.price*e.ud ).reduce( (e,acc) => e+acc ).toFixed(2) );
    }

    const deleteItem = id => { 
        ITEMS_DB.filter( e => e.id == id )[0].intoCart = false;
        setTotalPrice( (totalPrice - shoppingCart.filter( e => e.id == id ).map( e => e.price*e.ud )).toFixed(2) );
        setShoppingCart( shoppingCart.filter( e => e.id !== id ) );
    }

    const addItem = item => {
        ITEMS_DB.filter( e => e.id == item.id )[0].intoCart = true;
        shoppingCart.push( ITEMS_DB.filter( e => e.id == item.id )[0] ); //No sé la razón, pero si no hago este push el array no se actualiza a tiempo y el precio no cambia, porqué???
        setShoppingCart( shoppingCart );
        setTotalPrice( shoppingCart.map( e => e.price*e.ud ).reduce( (e,acc) => e+acc ).toFixed(2) );
    }

    return (
        <div>
            <div>
                <Nav />
                <Cart cart={shoppingCart} deleteItem={deleteItem} setUnitItem={setUnitItem} />
            </div>
            <div className="main-container scroll">
                <div className="container-list">
                    {
                        ITEMS_DB.map( (e,i) => <ItemList db={ITEMS_DB} item={e} id={i} addItem={addItem} /> )
                    }
                </div>
            </div>
        </div>
        
    )
}

export default Index;
